<?php require_once __DIR__ . '/../config/auth.php'; ensure_login(); ensure_role(['admin','manager']); $id=(int)($_POST['id']??0); if($id){ $st=$pdo->prepare('SELECT filename FROM job_photos WHERE job_id=?'); $st->execute([$id]); foreach($st as $r){ @unlink(__DIR__.'/../'.$r['filename']); } $pdo->prepare('DELETE FROM jobs WHERE id=?')->execute([$id]); log_event('job_delete','job',$id,null);} header('Location: '.url('jobs/index.php')); exit;